import React, {Component} from 'react';
import Playlist from '../components/Playlist';
import '../style/Playlist.css';

class Player extends Component {
    constructor(props){
        super(props);
        this.state = {
        moveActive: false,
        positionInDivX:0,
        positionInDivY:0,
        divX:10,
        divY:10,
        color:"grey",
        display: "none",
     }}

     

componentDidMount(){this.handlePlay()}

   handlePlay = () => {
    if(this.props.showPlaylistActive){
    this.setState({
       
        display:"block"
    })
    }       
   }

    handleMouseDown =(e) => {

        e.persist();

        this.setState(prevState => ({   
        moveActive: !prevState.moveActive,
           positionInDivX: e.nativeEvent.offsetX,
           positionInDivY: e.nativeEvent.offsetY,
           color:"grey"
        }));   
    };

    handleMouseMove = (e) => {

    e.persist();

     if (this.state.moveActive) {

    this.setState(prevState => ({   
        divX: e.clientX - this.state.positionInDivX,
        divY: e.clientY - this.state.positionInDivY,  
        color: "black",
        }));
       
    }
    };
    
    handleMouseUp = () => {
     
     this.setState(prevState => ({   
        moveActive: !prevState.moveActive,
        color: "grey"
     }));
};

    handleXClick = () => {
        console.log("klikX")
    }


    render() { 

        let div;
if(this.props.showPlaylistActive){
    div = <div className="player" onMouseDown={(e) => this.handleMouseDown(e)} onMouseMove={(e) => this.handleMouseMove(e)} onMouseUp={(e) => this.handleMouseUp(e)}
    style={divStyles}/>
}

else{
    div=<div className="player" onMouseDown={(e) => this.handleMouseDown(e)} onMouseMove={(e) => this.handleMouseMove(e)} onMouseUp={(e) => this.handleMouseUp(e)}
    style={divNone}/>
}

        
    
        return ( 
            <>
            {div}
            <div className="player" onMouseDown={(e) => this.handleMouseDown(e)} onMouseMove={(e) => this.handleMouseMove(e)} onMouseUp={(e) => this.handleMouseUp(e)}
             style={divStyles}>
            <Playlist/>
            <span className="x" onClick={this.handleXClick}>x</span>
            
            </div>
            </>
         );
    }
}
 
export default Player;
