import React, {Component} from 'react';
import '../style/Playlist.css';

class Player extends Component {
    state = {
        drawActive: false,
        insertDivX:"",
        insertDivY:"",
        divX:150,
        divY:50,
        left: `${divX}px`,
        top: `${divY}px`,
        color:grey,
     }

    Play = () => {

    const div = document.querySelector('div.player');
   
    
    this.handleMouseDown =(e) => {

        this.setState(prevState => ({   
           drawActive: true,
           insertDivX: e.offsetX,
           insertDivY: e.offsetY,
           color:yellow
        }));
    };


    this.handleMouseMove = (e) => {
     if (this.state.drawActive) {

    this.setState(prevState => ({   
        divX: e.clientX - insertDivX,
        divY: e.clientY - insertDivY,
        left: `${divX}px`,
        top: `${divY}px`,  
        }));}
    };
    
    this.handleMouseUp = () => {
     div.style.backgroundColor = "blue";
     this.setState(prevState => ({   
        // drawActive: !prevState.drawActive
        drawActive: false,
     }));
};
}

    render() { 
        return ( 
            <>
            <div className="player" onMouseDown={this.handleMouseDown} onMouseMove={this.handleMouseMove} onMouseUp={this.handleMouseUp}>
        
            </div>
            </>
         );
    }
}
 
export default Player;
