import React from 'react';
import './Offer.css';
import logo from '../trainersPictures/logo.png';

const variants = [
    {
        id: v0,
        title: 'fitrock open',
        gym: 'nieograniczony wstęp na siłownię',
        group: 'nieograniczony udział w zajęciach grupowych',
        trainer: null,
        price: '150zł'
    },

    {
        id: v1,
        title: 'fitrock gym',
        gym: 'nieograniczony wstęp na siłownię',
        group: null,
        trainer: null,
        price: '75zł'
    },

    {
        id: v2,
        title: 'fitrock gym premium',
        gym: 'nieograniczony wstęp na siłownię',
        group: null,
        trainer: 'plan treningowy i opieka trenera w pakiecie',
        price: '150zł'
    },

    {
        id: v3,
        title: 'fitrock group',
        gym: null,
        group: 'nieograniczony udział w zajęciach grupowych',
        trainer: null,
        price: '100zł'
    },

]


const Offer = () => {
let offer = variants.map(variant => (
<div className="offerContainer">
            <div className="offerContent">
                <h3>{variant.title}</h3>
                <img className="logoOffer" src={logo} />
                <p>{variant.gym}</p>
                <p>{variant.group}</p>
                <p>{variant.trainer}</p>
                <p>{variant.price}</p>
                <Button variant="danger"></Button>
            </div>
        </div>
))





    return (<>
        {offer}
    </>);
}

export default Offer;
