import React, {Component} from 'react';
import '../style/Playlist.css';

class Player extends Component {
    state = {
        moveActive: false,
        positionInDivX:0,
        positionInDivY:0,
        divX:10,
        divY:10,
        color:"grey",
     }

     handleProps =()=>{
         
     }

    handleMouseDown =(e) => {

        e.persist();

        this.setState(prevState => ({   
        moveActive: !prevState.moveActive,
           positionInDivX: e.nativeEvent.offsetX,
           positionInDivY: e.nativeEvent.offsetY,
           color:"grey"
        }));   
    };

    handleMouseMove = (e) => {

    e.persist();

     if (this.state.moveActive) {

    this.setState(prevState => ({   
        divX: e.clientX - this.state.positionInDivX,
        divY: e.clientY - this.state.positionInDivY,  
        color: "black",
        }));
       
    }
    };
    
    handleMouseUp = () => {
     
     this.setState(prevState => ({   
        moveActive: !prevState.moveActive,
        color: "grey"
     }));
};


    render() { 

        const divStyles = {
            backgroundColor:`${this.state.color}`,
            top: `${this.state.divY}px`,
            left: `${this.state.divX}px`,
        }
        return ( 
            <>
            <div className="player" onMouseDown={(e) => this.handleMouseDown(e)} onMouseMove={(e) => this.handleMouseMove(e)} onMouseUp={(e) => this.handleMouseUp(e)}
            style={divStyles}>
                <p>  {this.props.song.name.last} </p>
            </div>
            </>
         );
    }
}
 
export default Player;
