import React, {Component} from 'react';
import '../style/Playlist.css';

class Player extends Component {
    state = {
        drawActive: false,
        insertDivX:0,
        insertDivY:0,
        divX:150,
        divY:50,
        top:20,
        left:10,
        color:"grey",
     }

    handleMouseDown =(e) => {
        e.persist();
        this.setState(prevState => ({   
        drawActive: !prevState.drawActive,
           insertDivX: e.offsetX,
           insertDivY: e.offsetY,
           color:"yellow"
        }));   
        console.log(this.state.insertDivX)
    };


    handleMouseMove = (e) => {
    e.persist();
     if (this.state.drawActive) {

    this.setState(prevState => ({   
        divX: e.clientX -50, 
        // - this.state.insertDivX,
        divY: e.clientY -50,
        //  - this.state.insertDivY,
        // left: prevState.divX,
        // top:  prevState.divY,  
        color: "pink",
        }));
       
    }
    };
    
    handleMouseUp = () => {
     
     this.setState(prevState => ({   
        drawActive: !prevState.drawActive,
        color: "blue"
     }));
};


    render() { 

        // const {divX, divY} = this.state
        const divStyles = {
            backgroundColor:`${this.state.color}`,
            top: `${this.state.divY}px`,
            left: `${this.state.divX}px`,
            // top:divX,
            // left:divY,
        }
        return ( 
            <>
            <div className="player" onMouseDown={(e) =>this.handleMouseDown} onMouseMove={this.handleMouseMove} onMouseUp={this.handleMouseUp}
            style={divStyles}>
    
            </div>
            </>
         );
    }
}
 
export default Player;
