import React, {Component} from 'react';
import '../style/Playlist.css';

class Player extends Component {
    state = {
        drawActive: false,
        insertDivX:"",
        insertDivY:"",
        divX:150,
        divY:50,
        top:20,
        left:10,
        color:"grey",
     }

 

   
    
    handleMouseDown =(e) => {
        const div = document.querySelector('div.player');
        this.setState(prevState => ({   
           drawActive: true,
        //    insertDivX: e.offsetX,
        //    insertDivY: e.offsetY,
           color:"yellow"
        }));
    };


    handleMouseMove = (e) => {
     if (this.state.drawActive) {

    this.setState(prevState => ({   
        divX: e.clientX - prevState.insertDivX,
        divY: e.clientY - prevState.insertDivY,
        left: prevState.divX,
        top:  prevState.divY,  
        }));}
    };
    
    handleMouseUp = () => {
        const div = document.querySelector('div.player');  
     div.style.backgroundColor = "blue";
     this.setState(prevState => ({   
        // drawActive: !prevState.drawActive
        drawActive: false,
     }));
};


    render() { 

        const btnStyles = {
            backgroundColor:`${this.state.color}`,
            top: `${this.state.top}%`,
            left: `${this.state.left}%`
        }
        return ( 
            <>
            <div className="player" onClick={this.handleMouseDown} onMouseMove={this.handleMouseMove} onMouseUp={this.handleMouseUp}
            style={btnStyles}>
    
            </div>
            </>
         );
    }
}
 
export default Player;
