import {React, Component} from 'react';
import './Playlist.css';

const API = 'http://metallizer.dk/api/json/0'

class Playlist extends Component {
    state = { 
        album: null
     }
     handleDataFetch = () => {
        fetch(API)
          .then(response => {
            if (response.ok) {
              return response;
            }
            throw Error(response.status)
          })
          .then(response => response.json())
          .then(data => {
            this.setState({
              album: data.results
            })
    
          })
          .catch()
    render() 
    {
            const album = this.state
        return (
            <div> 
            <Button click = {this.handleDataFetch}/>
            {album ? <List album={album}/> : album}
            </div>
          );
    }
     }}
 
export default Playlist;
