import React, {Component} from 'react';
import '../style/Playlist.css';

class Player extends Component {
    state = {
        drawActive: false,
        insertDivX:0,
        insertDivY:0,
        divX:20,
        divY:50,
        color:"grey",
     }

    handleMouseDown =(e) => {

        e.persist();

        this.setState(prevState => ({   
        drawActive: !prevState.drawActive,
           insertDivX: e.nativeEvent.offsetX,
           insertDivY: e.nativeEvent.offsetY,
           color:"yellow"
        }));   
    };

    handleMouseMove = (e) => {

    e.persist();

     if (this.state.drawActive) {

    this.setState(prevState => ({   
        divX: e.clientX - this.state.insertDivX,
        divY: e.clientY - this.state.insertDivY,  
        color: "pink",
        }));
       
    }
    };
    
    handleMouseUp = () => {
     
     this.setState(prevState => ({   
        drawActive: !prevState.drawActive,
        color: "blue"
     }));
};


    render() { 

        const divStyles = {
            backgroundColor:`${this.state.color}`,
            top: `${this.state.divY}px`,
            left: `${this.state.divX}px`,
        }
        return ( 
            <>
            <div className="player" onMouseDown={(e) => this.handleMouseDown(e)} onMouseMove={(e) => this.handleMouseMove(e)} onMouseUp={(e) => this.handleMouseUp(e)}
            style={divStyles}>
    
            </div>
            </>
         );
    }
}
 
export default Player;
